import { Button, Dropdown } from "react-bootstrap";
import { useState} from "react";

function CustomSelect(props) {
  const options = props.options;
  const [value, setValue] = props.value;
  const [isOpened, setIsOpened] = useState(false);

  const strActiveStar = [];
  const strUnactiveStar = [];

  function set(str){
    setValue(str);
  }

  function setStyle(idx){
    if(parseInt(value)-1 === idx) return "dropdown-selected";
    return "dropdown-normal";
  }
  console.log(options[parseInt(value)]);

  return (
    <div style={{width: "100%", height: "100%", background: "red", display:"flex", flexDirection:"column", position:"relative"}}>
      <Button onClick={() => {setIsOpened(!isOpened)}}>{options[parseInt(value)-1].label}</Button>
      {(isOpened) &&
        <Dropdown>
            {options.map((state, index) => (
                <div key={index}>
                    <Dropdown.Item className={setStyle(index)} onClick={() => {setValue(state.value); console.log(value)}}>
                        {state.label}
                    </Dropdown.Item>
                </div>
            ))}
        </Dropdown>
      }
    </div>
  );
}

export default CustomSelect;
