import { Button, Dropdown } from "react-bootstrap";
import { useState} from "react";

function CustomSelect(props) {
  const options = props.options;
  const value = props.value;
  const [isOpened, setIsOpened] = useState(false);

  const strActiveStar = [];
  const strUnactiveStar = [];

  return (
    <div style={{width: "100%", height: "100%", background: "red", display:"flex", flexDirection:"column", position:"relative"}}>
      <Button onClick={() => {setIsOpened(!isOpened)}}>버튼</Button>
      {(isOpened) &&
        <Dropdown style={{marginTop:"100%", width: "100%", maxHeight: "300%", position:"absolute", overflowY:"scroll"}}>
            {options.map((state, index) => (
                <div key={index}>
                    <Dropdown.Item style={{width:"100%", height:"100%"}}>
                        {state.label}
                    </Dropdown.Item>
                    <Dropdown.Divider />
                </div>
            ))}
        </Dropdown>
      }
    </div>
  );
}

export default CustomSelect;
