import { useState, useEffect } from "react";
import Header from "../components/header";
import SearchMain from "../components/header-main";
import UserRating from "../components/user-rate";
import { Button, Row, Col, Form, Card, Image, Modal, Tabs, Tab } from "react-bootstrap";
import { useLocation } from "react-router-dom";

//import MultiRangeSlider from "../components/MultiRangeSlider"
import MultiRangeSlider from "multi-range-slider-react";


import "./home.css";
import { Link } from "react-router-dom";

function HomePage() {
  const { state } = useLocation();
  console.log(state);
  const [orderBy, setOrderBy] = useState("0");


  const handleChange = (event) => {
    setOrderBy(event.target.value)
  }

  const [showFilter, setShowFilter] = useState(false);
  const handleCloseFilter = () => setShowFilter(false);
  const handleShowFilter = () => setShowFilter(true);

  // For Like
  const [isLike, setIsLike] = useState("");

  // For checkboxes, 0-기본, 1~5-조기기구, 6-사이즈, 7-챌린지 제외, 8-챌린지만
  const filter_num = 9
  const [filterList, setFilterList] = useState([]);
  const [checkedState, setCheckedState] = useState(new Array(filter_num).fill(true));
  const handleOnChangeCheckedState = (position) => {
    const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : item
    );
    setCheckedState(updatedCheckedState);
  };

  // Price Range Slider
  const [minPriceValue, setMinPriceValue] = useState(3000);
  const [maxPriceValue, setMaxPriceValue] = useState(7000);
  const handleRangePriceInput = (e) => {
    setMinPriceValue(e.minValue);
    setMaxPriceValue(e.maxValue);
  };

  // Time Range Slider
  const [minTimeValue, setMinTimeValue] = useState(15);
  const [maxTimeValue, setMaxTimeValue] = useState(90);
  const handleRangeTimeInput = (e) => {
    setMinTimeValue(e.minValue);
    setMaxTimeValue(e.maxValue);
  };

  // filter check

  function FilterCheck() {
    var onfilter_list = [];
    for (let i=1; i<6; i++){
        if (checkedState[i] === false) {
            onfilter_list.push("기구");
            break;
        }
    }
    if(checkedState[6] === true) onfilter_list.push("사이즈");
    for (let i=7; i<9; i++){
        if (checkedState[i] === true) {
            onfilter_list.push("난이도");
            break;
        }
    }
   return onfilter_list;
  }

  useEffect(() => {
    setFilterList(FilterCheck());
  }, [checkedState]);

  return (
    <>
      <Header />
      <SearchMain />
      <div className="main-wrap">
        <div className="d-flex">
          <div className="py-2 w-75">
            <p className="d-flex flex-row">
                <Button variant="filter" onClick={handleShowFilter}></Button>
                {filterList.map((text) => (
                    <Button variant="filter-text" onClick={handleShowFilter}>{text}</Button>
                ))}
            </p>
            <p className="mb-0 mt-2">“뭉치”를 위한 Best 추천!!</p>
          </div>
          <Modal centered show={showFilter} onHide={handleCloseFilter} className="modal-filter">
            <Modal.Body>
              <div className="text-center"><Button className="filter-btn-close" variant="default" onClick={handleCloseFilter}><Image src="images/arrow-filter-icon.png" /></Button></div>

              <Tabs justify defaultActiveKey="basic" id="tab-filter" className="mb-3 tab-petkit tab-filter">
                <Tab eventKey="basic" title="기본">
                                  <Form.Group className="mb-0" controlId="timeCheckbox1">
                  <Form.Check type="checkbox" label="안심 레시피만 보기" checked={checkedState[0]} onChange={() => handleOnChangeCheckedState(0)} />
                  </Form.Group>
                  <p className="mt-4">사용 조리 기구</p>
                  <div className="my-3">
                    <Form.Check inline label="오븐" type="checkbox" checked={checkedState[1]} onChange={() => handleOnChangeCheckedState(1)} />
                    <Form.Check inline label="에어프라이어" type="checkbox" checked={checkedState[2]} onChange={() => handleOnChangeCheckedState(2)} />
                    <Form.Check inline label="믹서" type="checkbox" checked={checkedState[3]} onChange={() => handleOnChangeCheckedState(3)} />
                    <br></br>
                    <Form.Check inline label="찜기" type="checkbox" checked={checkedState[4]} onChange={() => handleOnChangeCheckedState(4)} />
                    <Form.Check inline label="전자레인지" type="checkbox" checked={checkedState[5]} onChange={() => handleOnChangeCheckedState(5)} />
                  </div>
                </Tab>
                <Tab eventKey="price" title="가격">
                  <Form.Label>가격</Form.Label>
                  <MultiRangeSlider
                    min={0}
                    max={10000}
                    step={1000}
                    ruler={true}
                    label={true}
                    preventWheel={true}
                    minValue={minPriceValue}
                    maxValue={maxPriceValue}
                    onInput={(e) => {
                      handleRangePriceInput(e);
                    }}
                  />
                </Tab>
                <Tab eventKey="size" title="사이즈">
                  <p className="mb-0">사이즈</p>
                  <Form.Group className="my-3" controlId="sizeCheckbox">
                    <Form.Check type="checkbox" label="L-size only" checked={checkedState[6]} onChange={() => handleOnChangeCheckedState(6)} />
                  </Form.Group>
                </Tab>
                <Tab eventKey="time" title="난이도/시간">
                  <p className="mb-3">난이도</p>
                  <Form.Group className="mb-0" controlId="timeCheckbox1">
                    <Form.Check type="checkbox" label="Challenge 제외하고 보기" checked={checkedState[7]} onChange={() => handleOnChangeCheckedState(7)} />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="timeCheckbox2">
                    <Form.Check type="checkbox" label="Challenge만 보기" checked={checkedState[8]} onChange={() => handleOnChangeCheckedState(8)} />
                  </Form.Group>
                  <Form.Label>시간</Form.Label>
                  <div className="mb-2">
                    <MultiRangeSlider
                      min={0}
                      max={160}
                      step={5}
                      ruler={true}
                      label={true}
                      preventWheel={true}
                      minValue={minTimeValue}
                      maxValue={maxTimeValue}
                      onInput={(e) => {
                        handleRangeTimeInput(e);
                      }}
                    />
                  </div>
                </Tab>
              </Tabs>
            </Modal.Body>
            <Modal.Footer className="d-inline-block">
              <div className="filter-button-section">
                <Row>
                  <Col xs={5}><div className="d-grid"><Button variant="default" size="xs">초기화</Button></div></Col>
                  <Col xs={7}><div className="d-grid"><Button variant="default" size="xs">적용하기</Button></div></Col>
                </Row>
              </div>
            </Modal.Footer>
          </Modal>

          <div className="ms-auto p-2">
            <Form.Select value={orderBy} onChange={handleChange}>
              <option value="1">추천순</option>
