  const [tabNum, setTabNum] = useState();


  const handleChange = (event) => {
    setOrderBy(event.target.value);
  }

  const handleTab = (text) => {
    setTabNum(text);
  }

  const [showFilter, setShowFilter] = useState(false);
  const handleCloseFilter = () => setShowFilter(false);
  const handleShowFilter = (text) => {
    if(text === "" || text === "기구"){
        setTabNum("basic");
        setShowFilter(true);
    }
    else if(text === "가격"){
        setTabNum("price");
        setShowFilter(true);
    }
    else if(text === "사이즈"){
        setTabNum("size");
        setShowFilter(true);
    }
    else if(text === "시간" || text === "난이도"){
        setTabNum("time");
        setShowFilter(true);
    }
  }
  // For Like
  const [isLike, setIsLike] = useState("");

  // For checkboxes, 0-기본, 1~5-조기기구, 6-사이즈, 7-챌린지 제외, 8-챌린지만
  const filter_num = 9
  const [filterList, setFilterList] = useState([]);
  const [checkedState, setCheckedState] = useState([true, true, true, true, true, true, false, false, false]);
  const handleOnChangeCheckedState = (position) => {
  const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : item
    );
    setCheckedState(updatedCheckedState);
  };

  // Price Range Slider
  const minPrice = 0;
  const maxPrice = 15000;
  const [minPriceValue, setMinPriceValue] = useState(minPrice);
  const [maxPriceValue, setMaxPriceValue] = useState(maxPrice);
  const handleRangePriceInput = (e) => {
    setMinPriceValue(e.minValue);
    setMaxPriceValue(e.maxValue);
  };

  // Time Range Slider
  const minTime = 0;
  const maxTime = 220;
  const [minTimeValue, setMinTimeValue] = useState(minTime);
  const [maxTimeValue, setMaxTimeValue] = useState(maxTime);
  const handleRangeTimeInput = (e) => {
    setMinTimeValue(e.minValue);
    setMaxTimeValue(e.maxValue);
  };

  // Product List
  const [prodBigList, setProdBigList] = useState([]);
  const [prodSmallList, setProdSmallList] = useState([]);
  // filter check

  function FilterCheck() {
    var onfilter_list = [];
    for (let i=1; i<6; i++){
        if (checkedState[i] === false) {
            onfilter_list.push("기구");
            break;
        }
    }
    if (minPriceValue !== minPrice || maxPriceValue !== maxPrice) onfilter_list.push("가격");
    if(checkedState[6] === true) onfilter_list.push("사이즈");
    if (minTimeValue !== minTime || maxTimeValue !== maxTime) onfilter_list.push("시간");
    for (let i=7; i<9; i++){
        if (checkedState[i] === true) {
            onfilter_list.push("난이도");
            break;
        }
    }
   return onfilter_list;
  }

  function MakeProductList(){
    var prod_list_big = [
        {
            name: "캐롭 바나나 케이크",
            price: 6000,
            Lprice: 2000,
            rating: 3,
            ratingNum: 30,
            imageURL: "images/products/carobbananacake.png",
            isLike: true,
            challenging: false
        },
        {
            name: "캐롭쿠키",
            price: 4000,
            Lprice: 2000,
            rating: 5,
            ratingNum: 300,
            imageURL: "images/products/carobcookie.png",
            isLike: false,
            challenging: true
        },
        {
            name: "단호박파이",
            price: 7000,
            Lprice: 2000,
            rating: 0,
            ratingNum: 0,
            imageURL: "images/products/pumpkinpie.png",
            isLike: false,
            challenging: true
        },
    ]

    var prod_list_small = [
        {
            name: "과일 아이스크림",
            price: 6000,
            Lprice: 2000,
            rating: 3,
            ratingNum: 30,
            imageURL: "images/products/berryicecream.png",
            isLike: true,
            challenging: false
        },
        {
            name: "리코타치즈",
            price: 3000,
            Lprice: "",
            rating: 1,
            ratingNum: 3300,
            imageURL: "images/products/ricottacheese.png",
            isLike: false,
            challenging: true
        }
    ]

    setProdBigList(prod_list_big);
    setProdSmallList(prod_list_small);
  }

  useEffect(() => {
    setFilterList(FilterCheck());
    MakeProductList();
  }, [checkedState, minPriceValue, maxPriceValue, minTimeValue, maxTimeValue]);

  return (
    <>
      <Header />
      <SearchMain />
      <div className="main-wrap overflow-hidden">
        <div className="d-flex flex-row">
          <div className="py-2 w-100">
            <p className="d-flex flex-row align-items-center">
                <Button variant="filter" onClick={() => {handleShowFilter("");}}></Button>
                {filterList.map((text) => (
                    <Button variant="filter-text" onClick={() => {handleShowFilter(text);}}>{text}</Button>
                ))}
                <div className="ms-auto p-2">
                  <Form.Select style = {{textAlign:"center", fontSize:"13px"}}value={orderBy} onChange={handleChange}>
                    <option value="1">추천순</option>
                    <option value="2">인기순</option>
                    <option value="3">최신순</option>
                    <option value="4">조리짧은순</option>
                  </Form.Select>
                </div>
            </p>
            {orderBy === "1" && <p className="mb-0 mt-2">“뭉치”를 위한 best 추천!</p>}
          </div>
          <Modal centered show={showFilter} onHide={handleCloseFilter} className="modal-filter">
            <Modal.Body>
              <div className="text-center"><Button className="filter-btn-close" variant="default" onClick={handleCloseFilter}><Image src="images/arrow-filter-icon.png" /></Button></div>

              <Tabs justify activeKey={tabNum} defaultActiveKey="basic" id="tab-filter" className="mb-3 tab-petkit tab-filter" onSelect={handleTab}>
                <Tab eventKey="basic" title="기본">
                  <Form.Group className="mb-0" controlId="basic1">
                    <Form.Check type="checkbox" label="안심 레시피만 보기" checked={checkedState[0]} onChange={() => handleOnChangeCheckedState(0)} />
                  </Form.Group>
                  <p className="mt-4">사용 조리 기구</p>
                  <div className="my-3">
                    <Form.Check inline label="오븐" id = "oven" type="checkbox" checked={checkedState[1]} onChange={() => handleOnChangeCheckedState(1)} />
                    <Form.Check inline label="에어프라이어" id = "airfyer" type="checkbox" checked={checkedState[2]} onChange={() => handleOnChangeCheckedState(2)} />
                    <Form.Check inline label="믹서" id = "mixer" type="checkbox" checked={checkedState[3]} onChange={() => handleOnChangeCheckedState(3)} />
                    <br></br>
                    <Form.Check inline label="찜기" id = "steamer" type="checkbox" checked={checkedState[4]} onChange={() => handleOnChangeCheckedState(4)} />
                    <Form.Check inline label="전자레인지" id = "micro" type="checkbox" checked={checkedState[5]} onChange={() => handleOnChangeCheckedState(5)} />
                  </div>
                </Tab>
                <Tab eventKey="price" title="가격">
                  <Form.Label>가격</Form.Label>
                  <MultiRangeSlider
                    min={0}
                    max={15000}
                    step={1000}
                    ruler={true}
                    label={true}
                    preventWheel={true}
                    minValue={minPriceValue}
                    maxValue={maxPriceValue}
                    onInput={(e) => {
                      handleRangePriceInput(e);
                    }}
                  />
                </Tab>
                <Tab eventKey="size" title="사이즈">
                  <p className="mb-0">사이즈</p>
                  <Form.Group className="my-3" controlId="sizeCheckbox">
                    <Form.Check type="checkbox" label="L-size only" checked={checkedState[6]} onChange={() => handleOnChangeCheckedState(6)} />
                  </Form.Group>
                </Tab>
                <Tab eventKey="time" title="난이도/시간">
                  <p className="mb-3">난이도</p>
                  <Form.Group className="mb-0" controlId="timeCheckbox1">
                    <Form.Check type="checkbox" label="Challenge 제외하고 보기" checked={checkedState[7]} onChange={() => handleOnChangeCheckedState(7)} />
                  </Form.Group>
                  <Form.Group className="mb-3" controlId="timeCheckbox2">
                    <Form.Check type="checkbox" label="Challenge만 보기" checked={checkedState[8]} onChange={() => handleOnChangeCheckedState(8)} />
                  </Form.Group>
                  <Form.Label>시간</Form.Label>
                  <div className="mb-2">
                    <MultiRangeSlider
                      min={0}
                      max={220}
                      step={20}
                      ruler={true}
                      label={true}
                      preventWheel={true}
                      minValue={minTimeValue}
                      maxValue={maxTimeValue}
                      onInput={(e) => {
                        handleRangeTimeInput(e);
                      }}
                    />
                  </div>
                </Tab>
              </Tabs>
            </Modal.Body>
            <Modal.Footer className="d-inline-block">
              <div className="filter-button-section">
                <Row>
                  <Col xs={5}><div className="d-grid"><Button variant="default" size="xs">초기화</Button></div></Col>
                  <Col xs={7}><div className="d-grid"><Button variant="default" size="xs">적용하기</Button></div></Col>
                </Row>
              </div>
            </Modal.Footer>
          </Modal>
        </div>
        <div className="products">
            {prodBigList.map((obj, index) =>
                <MainProduct type="0" obj={obj} />
            )}
            {
            <div className="product-list">
                <Row>
                    {prodSmallList.map((obj, index) =>
                        <MainProduct type="1" obj={obj} />
                    )}
                </Row>
            </div>
            }
        </div>

        <div className="cart-wrap">
          <Link to="/cart">
            <Image src="images/cart-icon.png" className="cart-icon" />
            <span className="cart-product-item-count"><span>1</span></span>
          </Link>
        </div>
      </div>
    </>
  );
}

export default HomePage;
