import { useState, useEffect } from "react";
import Header from "../components/header";
import SearchMain from "../components/header-main";
import UserRating from "../components/user-rate";
import { Button, Row, Col, Form, Card, Image, Modal, Tabs, Tab } from "react-bootstrap";
import { useLocation } from "react-router-dom";

//import MultiRangeSlider from "../components/MultiRangeSlider"
import MultiRangeSlider from "multi-range-slider-react";


import "./home.css";
import { Link } from "react-router-dom";

function HomePage() {
  const { state } = useLocation();
  console.log(state);
  const [orderBy, setOrderBy] = useState("0");
  const [tabNum, setTabNum] = useState();


  const handleChange = (event) => {
    setOrderBy(event.target.value);
  }

  const handleTab = (text) => {
    setTabNum(text);
  }

  const [showFilter, setShowFilter] = useState(false);
  const handleCloseFilter = () => setShowFilter(false);
  const handleShowFilter = (text) => {
    if(text === "" || text === "기구"){
        setTabNum("basic");
        setShowFilter(true);
    }
    else if(text === "가격"){
        setTabNum("price");
        setShowFilter(true);
    }
    else if(text === "사이즈"){
        setTabNum("size");
        setShowFilter(true);
    }
    else if(text === "시간" || text === "난이도"){
        setTabNum("time");
        setShowFilter(true);
    }
  }
  // For Like
  const [isLike, setIsLike] = useState("");

  // For checkboxes, 0-기본, 1~5-조기기구, 6-사이즈, 7-챌린지 제외, 8-챌린지만
  const filter_num = 9
  const [filterList, setFilterList] = useState([]);
  const [checkedState, setCheckedState] = useState([true, true, true, true, true, true, false, false, false]);
  const handleOnChangeCheckedState = (position) => {
  const updatedCheckedState = checkedState.map((item, index) =>
      index === position ? !item : item
    );
    setCheckedState(updatedCheckedState);
  };

  // Price Range Slider
  const minPrice = 0;
  const maxPrice = 15000;
  const [minPriceValue, setMinPriceValue] = useState(minPrice);
  const [maxPriceValue, setMaxPriceValue] = useState(maxPrice);
  const handleRangePriceInput = (e) => {
    setMinPriceValue(e.minValue);
    setMaxPriceValue(e.maxValue);
  };

  // Time Range Slider
  const minTime = 0;
  const maxTime = 220;
  const [minTimeValue, setMinTimeValue] = useState(minTime);
  const [maxTimeValue, setMaxTimeValue] = useState(maxTime);
  const handleRangeTimeInput = (e) => {
    setMinTimeValue(e.minValue);
    setMaxTimeValue(e.maxValue);
  };

  // filter check

  function FilterCheck() {
    var onfilter_list = [];
    for (let i=1; i<6; i++){
        if (checkedState[i] === false) {
            onfilter_list.push("기구");
            break;
        }
    }
    if (minPriceValue !== minPrice || maxPriceValue !== maxPrice) onfilter_list.push("가격");
    if(checkedState[6] === true) onfilter_list.push("사이즈");
    if (minTimeValue !== minTime || maxTimeValue !== maxTime) onfilter_list.push("시간");
    for (let i=7; i<9; i++){
        if (checkedState[i] === true) {
            onfilter_list.push("난이도");
            break;
        }
    }
   return onfilter_list;
  }

  useEffect(() => {
    setFilterList(FilterCheck());
  }, [checkedState, minPriceValue, maxPriceValue, minTimeValue, maxTimeValue]);

  return (
    <>
      <Header />
      <SearchMain />
      <div className="main-wrap overflow-hidden">
        <div className="d-flex flex-row">
          <div className="py-2 w-100">
            <p className="d-flex flex-row align-items-center">
                <Button variant="filter" onClick={() => {handleShowFilter("");}}></Button>
                {filterList.map((text) => (
                    <Button variant="filter-text" onClick={() => {handleShowFilter(text);}}>{text}</Button>
                ))}
                <div className="ms-auto p-2">
                  <Form.Select style = {{textAlign:"center", fontSize:"13px"}}value={orderBy} onChange={handleChange}>
                    <option value="1">추천순</option>
                    <option value="2">인기순</option>
                    <option value="3">최신순</option>
                    <option value="4">조리짧은순</option>
                  </Form.Select>
                </div>
            </p>
            {orderBy === "0" && <p className="mb-0 mt-2">“뭉치”를 위한 Best 추천!!</p>}
          </div>
          <Modal centered show={showFilter} onHide={handleCloseFilter} className="modal-filter">
            <Modal.Body>
              <div className="text-center"><Button className="filter-btn-close" variant="default" onClick={handleCloseFilter}><Image src="images/arrow-filter-icon.png" /></Button></div>

              <Tabs justify activeKey={tabNum} defaultActiveKey="basic" id="tab-filter" className="mb-3 tab-petkit tab-filter" onSelect={handleTab}>
                <Tab eventKey="basic" title="기본">
                  <Form.Group className="mb-0" controlId="basic1">
                    <Form.Check type="checkbox" label="안심 레시피만 보기" checked={checkedState[0]} onChange={() => handleOnChangeCheckedState(0)} />
                  </Form.Group>
                  <p className="mt-4">사용 조리 기구</p>
                  <div className="my-3">
                    <Form.Check inline label="오븐" id = "oven" type="checkbox" checked={checkedState[1]} onChange={() => handleOnChangeCheckedState(1)} />
                    <Form.Check inline label="에어프라이어" id = "airfyer" type="checkbox" checked={checkedState[2]} onChange={() => handleOnChangeCheckedState(2)} />
