import { useState, useEffect } from "react";
import UserRating from "../components/user-rate";
import { Button, Row, Col, Form, Image, Pagination, Tabs, Tab, Table, Modal } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faMinus } from '@fortawesome/free-solid-svg-icons';
import { faPlus } from '@fortawesome/free-solid-svg-icons';

import HeaderDetail from "../components/header-detail";

import "./product.css";


function DetailPage(props) {
  const navigate = useNavigate();

  const [isLike, setIsLike] = useState(true);

  let active = 2;
  let items = [];
  for (let number = 1; number <= 5; number++) {
    items.push(
      <Pagination.Item key={number} active={number === active}>
        {number}
      </Pagination.Item>,
    );
  }

  /* For Modal Q&A */
  const [showQA, setShowQA] = useState(false);
  const handleCloseQA = () => setShowQA(false);
  const handleShowQA = () => setShowQA(true);

  /* For Modal Cart */
  const [showCart, setShowCart] = useState(false);
  const handleCloseCart = () => setShowCart(false);
  const handleShowCart = () => setShowCart(true);

  /* For increase/descrease */
  const [inputNum, setInputNum] = useState(1);

  /* Data - ingred, recipe info */
  const [ingredData, setIngredData] = useState([]);
  const [recipeData, setRecipeData] = useState([]);

  /*Data - review*/
  const [reviewNum, setReviewNum] = useState(0);
  const [reviewData, setReviewData] = useState([]);

  function getData() {
    let ingredList = [{
        name: "블루베리",
        amt: "20g"
    }, {
        name: "산딸기",
        amt: "45g"
    }, {
        name: "락토프리 우유",
        amt: "40ml/40ml"
    }]

    let recipeList = [{
      imageURL: "prod0/1.png",
      contents: "블루베리(20g)와 우유(40ml)를 믹서기에 넣고 갈아주세요."
    },{
      imageURL: "prod0/2.png",
      contents: "산딸기(45g)와 우유(40ml)를 믹서기에 넣고 갈아주세요."
    }]

    let reviewList = [{
      user: "신*호",
      score: 5,
      imageURL: "prod0/1.png",
      date: "2022.01.05",
      contents: "개맛있음"
    },{
      user: "신*호",
      score: 4,
      imageURL: "prod0/1.png",
      date: "2022.01.01",
      contents: "신년~~"
    },{
      user: "Al*x",
      score: 5,
      imageURL: "prod0/1.png",
      date: "2021.12.25",
      contents: "크리스마스~"
    },{
      user: "구*진",
      score: 1,
      imageURL: "prod0/1.png",
      date: "2021.12.22",
      contents: "개맛없음"
    }, {
      user: "구*진",
      score: 1,
      imageURL: "prod0/1.png",
      date: "2021.12.22",
      contents: "개맛없음"
    }, {
      user: "구*진",
      score: 1,
      imageURL: "prod0/1.png",
      date: "2021.12.22",
      contents: "개맛없음"
    }]

    setIngredData(ingredList);
    setRecipeData(recipeList);

    setReviewNum(23);
    setReviewData(reviewList);
  }

  useEffect(() => {
    getData();
  }, []);

  function share() {
    console.log("share");
  }


  return (
    <>
    <HeaderDetail/>
    <div className="product-detail-wrap">
      <Row>
        <Col xs={12}>
          <Image src="images/prod0/main.png" className="product-image" alt="product-main-image"/>
          <div style={{display: "flex", width: "100%", justifyContent: "space-between", alignItems: "center"}}>
            <div className="d-flex">
                <span className="product-challenging" id="product-challenging-detail">도전!</span>
                <span className="product-detail-time"><span style={{marginTop: "2px"}}>125분</span></span>
            </div>
            <div className="d-flex">
                <Image src="images/share-icon.svg" className="product-detail-share" alt="share social" onClick={() => share()}/>
            </div>
          </div>
          <p className="product-title detail mt-1">캐롭바나나케이크</p>
          <p className="text-neoeb text-medium text-black mt-2">Kit: 6,000원 <span className="text-neom text-small text-black">(L-size +2000원)</span> </p>
          <p className="text-neom text-small text-gray">“어쩌구 저쩌구 저쩌구 우짜구 자짜구 맛있는 맛있는 피자” (요약, 설명)</p>
          <p className="text-neom text-smallmedium text-blackgray text-center">“뭉치”의 몇일치 간식이에요!!</p>
        </Col>
      </Row>

      <Row>
        <Col xs={12}>
          <Tabs justify defaultActiveKey="recipe" id="tab-product-detail" className="mt-3 mb-3 tab-petkit tab-product-detail">
            <Tab eventKey="recipe" tabClassName="tab-2text" title="레시피">
              <h1 className="product-detail-title text-neosb text-smallmedium text-halfblack">재료</h1>
              {ingredData.map((state, index) =>
                <div className="product-ingredtable mt-2">
                  <span className="text-neor text-small text-black">{state.name}</span>
                  <span className="text-neor text-small text-lightgray">{state.amt}</span>
                </div>
              )}
              <h1 className="product-detail-title mb text-neosb text-smallmedium text-halfblack">레시피</h1>
              {recipeData.map((state, index) =>
                <div>
                  <p className="mb-0"><Image src={"images/"+state.imageURL} className="product-image" /></p>
                  <p className="steptext text-neosb text-smallmedium text-halfblack">{"Step "+(parseInt(index)+1)}</p>
                  <p className="text-neor text-small text-black">{state.contents}</p>
                </div>
              )}
            </Tab>

            <Tab eventKey="kit-information" tabClassName="tab-2text" title="키트 정보">
                <Image src="images/prod0/kitinfo.jpg" className="product-image" alt="product-main-image"/>
            </Tab>

            <Tab eventKey="review" tabClassName="tab-2text" title="리뷰">
              <div>
                <p className="mb-0 text-neosb text-smallmedium text-halfblack">{"리뷰("+String(reviewNum)+")"}</p>
                <UserRating rate={4} show="on" align="center" />
              </div>
              {reviewData.map((review, index) =>
                  <div key={index}>
                    <p className="mb-0">{review.user}</p>
                    <div className="d-flex">
                      <div className="flex-grow-1"><UserRating rate={review.score} size="sm" /></div>
                      <div className="user-rate-date">2021.11.01</div>
                    </div>
                  </div>
              )}
                <p className="mb-0">신*호</p>
                <div className="d-flex">
                  <div className="flex-grow-1"><UserRating rate={5} size="sm" /></div>
                  <div className="user-rate-date">2021.11.01</div>
                </div>
                <div>
                  <p>구매 정보: Lx1, Mx1</p>
                  <Row>
                    <Col xs={4}><Image src="images/products/product-1.png" className="img-fluid" alt="product review" /></Col>
                    <Col xs={8}>요리해보니 사진과 거의 똑같이 나 왔어요. 뭉치가 너무 좋아하네요</Col>
                    <Col xs={12}>
                      <p>리뷰 5개 까지</p>
                    </Col>
                    <Col xs={12}>
                      <div className="review-pagination"><Pagination>{items}</Pagination></div>
                    </Col>
                  </Row>
                </div>
            </Tab>

            <Tab eventKey="qna" tabClassName="tab-2text" title="Q&A">
              <div>
                <h5>배송/환불/교환</h5>
                <p>안내 어쩌구 저쩌구~~~~</p>
              </div>

              <div>
                <p>문의</p>

                <Row className="form-review">
                  <Col xs={8}>
                    <Form.Group className="mb-3" controlId="your-review">
                      <Form.Control as="textarea" rows={2} placeholder="상품 관련 질문을 등록해주세요." />
                    </Form.Group>
                  </Col>
                  <Col xs={4}>
                    <Button variant="default" onClick={handleShowQA}>등록</Button>
                  </Col>
                </Row>

                <Modal centered className="modal-qa" show={showQA} onHide={handleCloseQA}>
                  <Modal.Body>질문을 등록하시겠습니까?</Modal.Body>
                  <Modal.Footer className="justify-content-center">
                    <Button variant="primary" onClick={handleCloseQA}>등록</Button>
                    <Button variant="secondary" onClick={handleCloseQA}>취소</Button>
                  </Modal.Footer>
                </Modal>

                <div>
                  <p>질문: 양배추는 몇 g 오나요?</p>
                  <p>답변: 2kg 드립니다^^~~</p>
                </div>
              </div>

            </Tab>
          </Tabs>
        </Col>
      </Row>
    </div>

    <div className="product-cart-button">
      <span className={isLike ? "product-likedetail active" : "product-likedetail"}  onClick={() => setIsLike(!isLike)}></span>
      <Button variant="primary" className="text-neom text-xxsmallmedium" style={{width:"285px", height:"44px"}} onClick={handleShowCart}>구매하기</Button>

      <Modal centered className="modal-cart" show={showCart} onHide={handleCloseCart}>
        <Modal.Body>
          <div className="modal-cart-item">
            <div>닭가슴살 브로콜리 고구마 도우 크랜베리 피자 - Regular size</div>
            <div className="d-flex">
              <div className="flex-grow-1">
                <div>
                  <button className="btn-descrease"><FontAwesomeIcon icon={faMinus} className="" /></button>
                  <input type="text" className="cart-item-number" value={inputNum} onChange={(e) => setInputNum(e.target.value)} />
                  <button className="btn-increase"><FontAwesomeIcon icon={faPlus} className="" /></button>
                </div>
              </div>
              <div className="modal-cart-price">6,000원</div>
            </div>
          </div>
          <div className="modal-cart-item">
            <div>닭가슴살 브로콜리 고구마 도우 크랜베리 피자 - Regular size</div>
            <div className="d-flex">
              <div className="flex-grow-1">
                <div>
                  <button className="btn-descrease"><FontAwesomeIcon icon={faMinus} className="" /></button>
                  <input type="text" className="cart-item-number" value={inputNum} onChange={(e) => setInputNum(e.target.value)} />
                  <button className="btn-increase"><FontAwesomeIcon icon={faPlus} className="" /></button>
                </div>
              </div>
              <div className="modal-cart-price">6,000원</div>
            </div>
          </div>
          <div className="modal-cart-item-total">총 상품금액 14,000원</div>
        </Modal.Body>
        <Modal.Footer className="justify-content-center">
          <Button variant="outline-warning" onClick={handleCloseCart}>장바구니</Button>
          <Button variant="primary" onClick={() => navigate("/order")}>바로구매</Button>
        </Modal.Footer>
      </Modal>
    </div>
    </>
  );
}

export default DetailPage;
