                </Form.Group>
                <Button variant="purple" className="text-neom text-xxsmallmedium" style={{letterSpacing: "2.24px", width: "80px", height: "40px"}}onClick={handleShowQA}>등록</Button>
              </div>
              <div style={{marginTop:"52px"}}>
