import { Button, Dropdown } from "react-bootstrap";
import { useState, useEffect} from "react";

function CustomSelect(props) {
  const options = props.options;
  const [value, setValue] = props.value;
  const [isOpened, setIsOpened] = useState(false);
  const [mainText, setMainText] = useState("");

  const strActiveStar = [];
  const strUnactiveStar = [];

  if(props.size==="sm") {
    var size="small";
    var wrap="dropdownsmall-wrap";
    var textalign="center";
  }
  else if(props.size==="lg") {
    var size="large";
    var wrap="dropdownlarge-wrap";
  }

  function set(str){
    setValue(str);
    localStorage.setItem("sort", str);
  }

  function setStyle(idx){
    if(parseInt(value)-1 === idx) return "dropdown-selected";
    return "dropdown-normal";
  }
  useEffect(() => {
    if(props.placeholder) setMainText(props.placeholder);
    else setMainText(options[parseInt(value)-1].label);
  }, []);

  useEffect(() => {
    setMainText(options[parseInt(value)-1].label);
  }, [value]);

  return (
    <>
    { isOpened &&
    <div style={{width:"100vw", height:"100vh", position:"fixed", zIndex:"1", left: "0", top: "0"}} onClick={() => {setIsOpened(false)}}>
    </div>
    }
    <div className={wrap}>
      <Button variant="default" onClick={() => {setIsOpened(!isOpened)}}>{mainText}</Button>
      {(isOpened) &&
        <Dropdown className={size}>
            {options.map((state, index) => (
                <div key={index}>
                    <Dropdown.Item className={setStyle(index)} style={{justifyContent:props.textalign}} onClick={() => {set(state.value);}}>
                        {state.label}
                    </Dropdown.Item>
                </div>
            ))}
        </Dropdown>
      }
    </div>
    </>
  );
}

export default CustomSelect;
